import React, { useState } from "react";

function Component() {
  const [isEmailInValid, setIsEmailInValid] = useState(false);

  function emailHandler(e) {
    let email = e.target.value;
    if (!email.includes("@")) {
      setIsEmailInValid(true);
    } else {
      setIsEmailInValid(false);
    }
  }
  return (
    <form className="formContainer">
      <div>
        <label htmlFor="email">Email:</label>
        <input
          className="inputs"
          type="email"
          id="email"
          required
          onChange={emailHandler}
        />
        <p className="error">{isEmailInValid && "Invalid email!"}</p>
      </div>
      <div>
        <label htmlFor="password" onChange={passwordHandler}>
          Password:
        </label>
        <input className="inputs" type="password" id="password" required />
        <p className="error">
          {isEmailInValid && "password must be 8 characters long !"}
        </p>
      </div>
      <div>
        <label htmlFor="passwords" onChange={confirmPasswordHandler}>
          Confirm Password:
        </label>
        <input className="inputs" type="password" id="passwords" required />
        <p className="error">{isEmailInValid && "Invalid email!"}</p>
      </div>
      <button id="signupButton">Sign Up</button>
    </form>
  );
}

export default Component;
