import React, { useState } from "react";

function Component() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setconfirmPassword] = useState("");

  function emailHandler(e) {
    let email = e.target.value;
  }
  return (
    <form className="formContainer">
      <div>
        <label htmlFor="email">Email:</label>
        <input
          className="inputs"
          type="email"
          id="email"
          required
          onChange={emailHandler}
        />
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input className="inputs" type="password" id="password" required />
        {isEmailValid && <p className="error">Invalid email!</p>}
      </div>
      <div>
        <label htmlFor="passwords">Confirm Password:</label>
        <input className="inputs" type="password" id="passwords" required />
      </div>
      <button id="signupButton">Sign Up</button>
    </form>
  );
}

export default Component;
